const deleteMember=(Mem_ID)=>{
  console.log(Mem_ID);
  ManagerService.removeMember(Mem_ID)
  .then(response =>{
    console.log(response.data);
    refreshList();
  })
}

  return (
    <div className="list row">
      <div className="col-md-8">
        <div className="input-group mb-3">
         { 
          //  <input
