            //     </button>
            //   </div>
          }
        </div>
      </div>
      <div className="col-md-6" align="center" style={{ textAlign: "center" }}>
        <h4>Members List</h4>

        <Table striped bordered hover variant="dark">
          <thead>
            <tr>

              <th>Member id</th>
              <th>Member Name</th>
              <th>Member Phone</th>
              <th>Delete</th>
            </tr>
          </thead>
          {member &&
            member.map((member, index) => (
              <tr>
                <td>{member.Mem_ID}</td>
                <td>{member.Mem_Name}</td>
                <td>{member.Mobile_Number}</td>
                <td><Link
                  to={"/editmember/" + member.Mem_ID}
                  className="badge badge-warning">
                  Edit
                </Link><button className="m-3 btn-sm btn-danger" onClick={() => { deleteMember(member.Mem_ID) }}>Delete</button></td>
              </tr>
