import React, { useState, useRef } from "react";
import { useNavigate } from 'react-router-dom';
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";
import "./Member.css"
import AuthService from "../services/auth.service";

const required = (value) => {
  if (!value) {
    return (
      <div className="alert alert-danger" role="alert">
        This field is required!
      </div>
    );
  }
};

const Login = () => {
  let navigate = useNavigate();

  const form = useRef();
  const checkBtn = useRef();

  const [manager_Email, setManager_Email] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const onChangeManager_Email = (e) => {
    const manager_Email = e.target.value;
    setManager_Email(manager_Email);
  };

  const onChangePassword = (e) => {
    const password = e.target.value;
    setPassword(password);
  };

  const handleLogin = (e) => {
    e.preventDefault();

    setMessage("");
    setLoading(true);

    form.current.validateAll();

    if (checkBtn.current.context._errors.length === 0) {
      AuthService.login(manager_Email, password).then(
        (response) => {
          navigate("/profile/" + response.id);
          // console.log(response);


          window.location.reload();
        },
        (error) => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();

          setLoading(false);
          setMessage(resMessage);
        }
      );
    } else {
      setLoading(false);
    }
  };

  return (
    <div className="">
      <div className="">
        <img
          src="//ssl.gstatic.com/accounts/ui/avatar_2x.png"
          alt="profile-img"
          className="profile-img-card"
        />
        <div className="fom">
          <h3 className="fom">Login</h3>
          <Form onSubmit={handleLogin} ref={form}>
            <div className="">
              <label className="lbel" htmlFor="manager_Email">Manager_Email</label>
              <Input
                type="text"
                className="inp"
                name="manager_Email"
                value={manager_Email}
                onChange={onChangeManager_Email}
                validations={[required]}
              />
            </div>

            <div className="">
              <label className="lbel" htmlFor="password">Password</label>
              <Input
                type="password"
                className="inp"
                name="password"
                value={password}
                onChange={onChangePassword}
                validations={[required]}
              />
            </div>

            <div className="">
              <button className="" disabled={loading}>
                {loading && (
                  <span className=""></span>
                )}
                <span>Login</span>
              </button>
            </div>

            {message && (
              <div className="">
                <div className="alert alert-danger" role="alert">
                  {message}
                </div>
              </div>
            )}
            <CheckButton style={{ display: "none" }} ref={checkBtn} />
          </Form>
        </div>
      </div>
    </div>
  );
};

export default Login;
